import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";

const SearchBox = () => {
    const [companies, setCompanies] = useState([]);
    const handleChange = async () => {
        // const res = await axios.post(
        //     `https://www.mca.gov.in/mcafoportal/cinLookup.do`,
        //     { companyname: "brand" },
        //     {
        //         headers: {
        //             "Content-Type": "text/html",
        //             // "Content-Length": "551",
        //             "Access-Control-Allow-Origin":
        //                 "*",
        //         },
        //     }

        // );
        const res = await fetch('https://www.mca.gov.in/mcafoportal/cinLookup.do', {
          method: 'post',
          headers: {'Content-Type':'application/json'},
          body: {
           "companyname": "brand"
          }
         });
        // const res = await fetch(
        //   "https://www.zaubacorp.com/custom-search"
        // );
        const data = res.json();
        // console.log(data);
        console.log(data);
        setCompanies(res.data);
    };

    return (
        <Autocomplete
            id="free-solo-demo"
            onChange={handleChange}
            options={companies.map((option) => option.title)}
            renderInput={(params) => (
                <TextField onChange={handleChange} {...params} label="Search" />
            )}
        />
    );
};

export default SearchBox;
