import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";

const SearchBox = () => {
    const [companies, setCompanies] = useState([]);
    const handleChange = async () => {
        const res = await axios.post(
            `https://www.zaubacorp.com/custom-search`,
            { search: "b", filter: "company" },
            {
                headers: {
                    "Content-Type": "text/html",
                    "Content-Length": "551",
                    "Access-Control-Allow-Origin":
                        "https://www.zaubacorp.com https://can.zaubacorp.com https://zaubacorp.com",
                },
            }
        );
        // const res = await fetch(
        //   "https://www.zaubacorp.com/custom-search"
        // );
        // const data = await res.json();
        // console.log(data);
        console.log(res.data);
        setCompanies(res.data);
    };

    return (
        <Autocomplete
            id="free-solo-demo"
            onChange={handleChange}
            options={companies.map((option) => option.title)}
            renderInput={(params) => (
                <TextField onChange={handleChange} {...params} label="Search" />
            )}
        />
    );
};

export default SearchBox;
