import React, { useState } from "react";
import TextField from "@mui/material/TextField";
import Autocomplete from "@mui/material/Autocomplete";
import axios from "axios";

const SearchBox = () => {
    const [companies, setCompanies] = useState([]);
    const handleChange = async (event) => {
        const res = await axios.post(
            `http://localhost:3001`,
            { companyName: event.target.value.toLowerCase() },
            {
                headers: {
                    "Content-type": "application/json",
                },
            }
        );
        // const res = await fetch(
        //   "https://www.zaubacorp.com/custom-search"
        // );
        // const data = await res.json();
        console.log(res.data);
        let companyList = []
        let matchString = /<.*id="(.*)".*>(.*)<\/div>/
        for(let i of res.data.split("\n")){
          let sanitizedData = i.replace(/(\t\n)|(\n\t)|(\t)/gm,'')
          let matchStringWithI = i.match(matchString)
          if(sanitizedData.length>0){
            companyList.push({
              "Name":matchStringWithI[1],
              "Link":matchStringWithI[0]
            })
          }
        }
        console.log(`Company List`+JSON.stringify(companyList))
        let data = res.data.replace(/<[^>]*>?|(\t\n)|(\n\t)|(\t)/gm, '').split(" ");
        console.log(data)
        const clearData = res.data.replace(/<[^>]*>?|(\t\n)|(\n\t)|(\t)/gm, '').split(" ");
        let removedSpaceData = clearData.filter(data=>data.length>0);
        setCompanies(removedSpaceData)
        // setCompanies(res.data);
    };

    return (
        <Autocomplete
            id="free-solo-demo"
            onChange={handleChange}
            options={companies.map((option) => option.title)}
            renderInput={(params) => (
                <TextField onChange={handleChange} {...params} label="Search" />
            )}
        />
    );
};

export default SearchBox;
